import React , { Component } from 'react';

class ListUser extends Component {

  state = {
    datauser : [],
    loading : true,
    error:false
  }


  componentDidMount(){
    fetch('https://www.baznaspayakumbuh.com/api/api_tabloit_pulsa.php')
    .then(response=>response.json()
    )
    .then(
      (data)=> { this.setState({ datauser:data}) }
    )}

  render(){


        const {error,loading,datauser} = this.state;
        if(error){
          return (<p>{error.message}</p>);
        }

        if(loading){
          return (<p>Loading...</p>);
        }

        const listuser = datauser.map(user=>{
          return (<div>{user.result.title}</div>);

        })


    return (
      <div>
        list Berita
      </div>
    )


  }

}

export default ListUser
