import React, { Component } from 'react';

class DetailUser extends Component{

  constructor(props){
    super(props);
    this.state = {
      datauser : []
    }
  }


componentDidMount(){
  const idnya = this.props.match.params.id_user ;
  fetch('https://www.baznaspayakumbuh.com/api/api_tabloit_pulsa.php?id='+idnya)
  .then(response=>response.json()
  )
  .then(
    (data)=> { this.setState({datauser:data.result}) }
  )
}

  render(){
    const listuser = this.state.datauser.map((user)=>{
      return (

          <div className="alert alert-primary">
            <Link to={'/detail/'+user.article_id}>
            <img src={user.image} width="100%"/>
            </Link>
              <h5 >{user.title}</h5>
              <p>{user.description}</p>

        </div>


      );
    })


  return (
  <div>
    {listuser}
  </div>
  )


  }

}

export default DetailUser
