import React , { Component } from 'react';

class ListUser extends Component {


constructor(props){
  super(props);
  this.state = {
    datauser : []
  }
}


  componentDidMount(){
    fetch('https://www.baznaspayakumbuh.com/api/api_tabloit_pulsa.php')
    .then(response=>response.json()
    )
    .then(
      (data)=> { this.setState({datauser:data.result}) }
    )
  }

  render(){


        const listuser = this.state.datauser.map((user)=>{
          return (
          <div class="container-fluid">
            <div className="row" >
              <div className="col-md-4" key={user.article_id}>
              <div className="card">
                <img src={user.image} className="card-img-top"/>
                <div className="card-body">
                  <h5 className="card-title">{user.title}</h5>
                  <p className="card-text">{user.description.substr(1, 150)}</p>
                </div>
              </div>
            </div>
          </div>
          </div>

          );
        })


    return (
      <div>
        {listuser}
      </div>
    )


  }

}

export default ListUser
